SELECT EMPLOYEE_ID, FIRST_NAME, LAST_NAME, JOB_ID, HIRE_DATE, format(SALARY, 'C2', 'id-ID') SALARY, case 	when month(HIRE_DATE)>month(getdate()) 		then 			concat(DATEDIFF(year, HIRE_DATE,GETDATE())-1,' Tahun ', 			DATEDIFF(MONTH, DATEADD(year, DATEDIFF(year, HIRE_DATE,GETDATE())-1, HIRE_DATE), GETDATE()),' Bulan')	ELSE 			concat(DATEDIFF(year, HIRE_DATE,GETDATE()),' Tahun ', 			DATEDIFF(MONTH, DATEADD(year, DATEDIFF(year, HIRE_DATE, GETDATE()), HIRE_DATE), GETDATE()),' Bulan')ENDMASA_KERJAFROM EMPLOYEES